import './style/App.css';
import TaskForm from "./app_components/TaskForm";
import Task from "./app_components/Task";
import { useEffect, useState } from "react";
import { AllTasksFilter, CompletedTasksFilter, IncompleteTasksFilter } from './app_components/FilterStrategy';

const App = () => {
    const [tasks, setTasks] = useState([]);
    const [filter, setFilter] = useState("toate"); // Starea pentru tipul de filtrare
    // Hook-ul useEffect pentru a salva sarcinile în localStorage atunci când se modifică starea tasks



    // Funcție pentru filtrarea sarcinilor
    const filterTasks = (tasks) => {
        let filterStrategy;

        if (filter === "finalizate") {
            filterStrategy = new CompletedTasksFilter();
        } else if (filter === "nefinalizate") {
            filterStrategy = new IncompleteTasksFilter();
        } else {
            filterStrategy = new AllTasksFilter();
        }

        return filterStrategy.filter(tasks);
    };
    useEffect(() => {
        if (tasks.length === 0) return;
        localStorage.setItem('tasks', JSON.stringify(tasks));
    }, [tasks]);


    useEffect(() => {
        const savedTasks = JSON.parse(localStorage.getItem('tasks'));
        setTasks(savedTasks || []);
    }, []);


   /* setTasks pentru a actualiza starea anterioară a obiectului tasks.*/
    const addTask = (name) => {
        setTasks(prev => {
            return [...prev, { name: name, done: false }];
        });
    }

    const removeTask = (indexToRemove) => {
        setTasks(prev => {
            return prev.filter((taskObject, index) => index !== indexToRemove);
        });
    }

    const updateTaskDone = (taskIndex, newDone) => {
        setTasks(prev => {
            const newTasks = [...prev];
            newTasks[taskIndex].done = newDone;
            return newTasks;
        });
    }

    const renameTask = (index, newName) => {
        setTasks(prev => {
            const newTasks = [...prev];
            newTasks[index].name = newName;
            return newTasks;
        })
    }

    return (
        <main>

            <h1>LISTĂ DE ACTIVITĂȚI</h1>
            <div className="filter">
                <label htmlFor="filterSelect">Filtrare: </label>
                <select id="filterSelect" value={filter} onChange={handleFilterChange}>
                    <option value="toate">Toate</option>
                    <option value="finalizate">Finalizate</option>
                    <option value="nefinalizate">Nefinalizate</option>
                </select>
            </div>
            <TaskForm onAdd={addTask} />
            {filterTasks(tasks).map((task, index) => (
                <Task
                    key={index}
                    {...task}
                    onRename={(newName) => renameTask(index, newName)}
                    onTrash={() => removeTask(index)}
                    onToggle={(done) => updateTaskDone(index, done)}
                />
            ))}


        </main>
    );
}
export default App;
